package iFElseIfAssignment;

public class IfConditionClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int saving = 1500;
		int closing = 2000;

		if (saving > closing) {
			System.out.println("You will get 12% interest");
		} else if (saving < closing) {
			System.out.println("You will get 2% interest");
		}

	}

}
